def create_freq_dist():
    freqs, dists = freq_dist['frequencies'], freq_dist['distances']
    root = join(expanduser('~'), 'Downloads')
    files = {
        60: 'mediane_60s_x_yaml',
        90: 'mediane_90s_x_yaml',
        120: 'mediane_120s_x_yaml',
        180: 'mediane_180s_x_yaml'
    }
    indent = '    '
    print('fdtable = { # frequencies-distance tables of spectral values')
    print(f'{indent}# distances (deg):')
    print(f'{indent}"dist": np.array({str(dists)}),')
    print(f'{indent}# frequencies (Hz):')
    print(f'{indent}"freq": np.array({str(freqs)}),')
    print(f'{indent}# min frequencies: window length in s (from theoretical P arriv. time) maopped to frequency (in Hz)')
    print(f'{indent}"freqmin": {str(fmin)},')
    print(f'{indent}# Spectral tables: window length in s (from theoretical P arriv. time)')
    print(f'{indent}# mapped to tables of spectral values. For each table:')
    print(f'{indent}#   row i denotes the values at dist[i]')
    print(f'{indent}#   column j denotes the values at freq[j]')
    for key, f in files.items():
        file = join(root, f)
        with open(file) as opn:
            content = yaml.safe_load(opn)
            assert len(freqs) == len(content[0])
            assert len(dists) == len(content)
            print(f'{indent}{key}: np.array([')
            for c in content:
                print(f'{indent}{indent}{str(c)},')
            print(f'{indent}]),')
    print('}')


